import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}


<div class="wrapper" id="topDiv">
  <header>
    <img src="assets/images/pxfuel2.jpg" class="background-back" />

    <h1 class="background-text text-white">Explore the wonders of Macedonia</h1>

    <button mat-raised-button class="mt-5" >
      Take me somewhere
    </button>
  </header>

</div>
